        return this.http.get('../../assets/rides.json')
            .map((response) => response.json())
            .catch(this.handleError);
    }
    getAllUsers() {
        return this.http.get('../../assets/users.json')
            .map((response) => response.json())
            .catch(this.handleError);
    }
